  </template>
  
  <script>
  import QRCode from 'qrcode'
  
  export default {
    data() {
      return {
        key: this.$route.query.key,
        linkDataOnLine: '',
        linkDataLocal: 'http://localhost:8080/build?key=' + this.$route.query.key,
        citys: '',
        isChecked: false,
        QRImgUrl: '',
        baseUrl: window.location.origin + '/build?key=',
        isDetails: false,
        linkText: ''
      }
    },
    created() {
      this.linkDataOnLine = this.baseUrl  + this.$route.query.key
      this.getQRcode()
      if(this.$route.query.type && this.$route.query.type === 'details') {
        this.isDetails = true
      }
    },
    methods: {
      saveImage() {
              // 创建一个虚拟链接
              const link = document.createElement('a');
              link.href = this.QRImgUrl;
              link.download = 'image.jpg'; // 下载的文件名
  
              // 模拟点击下载链接
              document.body.appendChild(link);
              link.click();
  
              // 移除虚拟链接
              document.body.removeChild(link);
          },
      async getQRcode() {
        let opts = {
          errorCorrectionLevel: "L", // 容错级别
          type: "image/png", // 生成的二维码类型
          quality: 0.3, // 二维码质量
          margin: 5, // 二维码留白边距
          width: 128, // 宽
          height: 128, // 高
          text: "http://www.xxx.com", // 二维码内容，确保this.QRlink包含有效的URL
          color: {
            dark: "#3f571f", // 前景色
            light: "#fff" // 背景色
          }
        };
  
        try {
          // 使用async/await来等待QRCode.toDataURL的结果
          this.QRImgUrl = await QRCode.toDataURL(this.linkDataOnLine, opts)
          // console.log(this.QRImgUrl)
        } catch (err) {
          console.error(err);
        }
      },
      copyLink() {
        var context = this.linkDataOnLine
        const el = document.createElement('textarea')
        el.value = context
        document.body.appendChild(el)
        el.select()
        document.execCommand('copy')
        document.body.removeChild(el)
        this.$message.success('复制成功：' + context)
      },
      openInNewTab() {
        // 在新选项卡中打开一个URL
        window.open(this.linkDataOnLine, '_blank')
      },
      openInNewTabTest() {
         // 在新选项卡中打开一个URL
         window.open(this.linkDataLocal, '_blank')
      },
      goback() {
        this.$router.push({ path: '/formDetails', query: { key: this.$route.query.key } })
      },
      gobackDetails() {
        this.$router.push({ path: '/form/details', query: { key: this.$route.query.key } })
      },
      changeCitys() {
        this.linkDataOnLine = 'https://tduck-vue.foxconnedu.com/s/a7378a8b499b4fbc923334bb0683c518?factoryarea=' + this.citys
      },
      addLogo(text) {
        this.linkDataOnLine = this.baseUrl  + this.$route.query.key
        if(text) {
          this.linkDataOnLine = this.linkDataOnLine + '?source=' + text
        }
      }
    }
  }
  </script>
  
